        plates: 'KDA001',
        routes: 'A-B',
        price: '5',
        departureTime: '2022-10-1214:34:08.7',
        arrivalTime: '2022-10-1214:38:08.7',
      };
      const res = await testServer.post('/api/v1/bus')
      .send(bDetails);

      expect(res.status).to.equal(401);

    });
  });

  describe('D', () => {
     // tslint:disable-next-line: ter-prefer-arrow-callback
    it('Fetches Bus details by Id', async function () {
      // tslint:disable-next-line: await-promise
      const cde = await prisma.bus.findOne({
        where: {
          plates: 'KDA001',
        },
      });
      const res = await testServer.get(`/api/v1/bus/${cde?.id}`)
      .auth(token, { type: 'bearer' });

      expect(res.status).to.be.equal(200);

    });
  });
  describe('E', () => {
     // tslint:disable-next-line: ter-prefer-arrow-callback
    it('Fails to fetch Bus Data', async function () {
       // tslint:disable-next-line: await-promise
      const cde = await prisma.bus.findOne({
        where: {
          plates: 'KDA001',
        },
      });
      const res = await testServer.get(`/api/v1/bus/${cde?.id}`);

      expect(res.status).to.be.equal(401);

    });

  });
});

describe('TRANSACTION TEST', () => {
  describe('A', () => {
     // tslint:disable-next-line: ter-prefer-arrow-callback
    it('Creates A payment instance', async function () {
       // tslint:disable-next-line: await-promise
      const cde = await prisma.bus.findOne({
        where: {
          plates: 'KDA001',
        },
      });
       // tslint:disable-next-line: await-promise
      const abc = await prisma.user.findOne({
        where: {
          email: 'user@user.com',
        },
      });
      const res = await testServer.get(`/api/v1/pay/${abc?.id}/${cde?.id}`)
      .auth(token, { type: 'bearer' });

      expect(res);

    });

  });

});

import express from 'express';
import bodyParser from 'body-parser';
import helmet from 'helmet';
import cors from 'cors';
import dotenv from 'dotenv';
import compression from 'compression';
import passport from 'passport';
import './auth/passport';

// tslint:disable-next-line:import-name
import userRoutes from './routes';
// tslint:disable-next-line:no-var-requires

dotenv.config();
const app: express.Application = express();

 // BODY-PARSER MIDDLEWARE
/*
* With express version =>4.16.0
* Body-parser middleware was added back under
* express.json() & express.urlencoded
 */

app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// CORS

app.use(cors());

// HELMET
app.use(helmet());

// COMPRESSION
app.use(compression());

// ROUTES MIDDLEWARE
app.use('/api/v1', userRoutes);

// LISTENING PORT

const PORT = process.env.PORT;

app.listen(PORT, () => {
  // tslint:disable-next-line:no-console
  console.log(`Server is listening on port ${PORT} 🔥🔥`);
});

export default app;

import passport from 'passport';
import passportJwt from 'passport-jwt';
import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();
const jwtStrategy = passportJwt.Strategy;
const ExtractJwt = passportJwt.ExtractJwt;

const opts: passportJwt.StrategyOptions = {
  jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken(),
  secretOrKey: process.env.SECRET_OR_KEY!,

};

passport.use(new jwtStrategy(opts, (payload, done) => {
  prisma.user.findOne({ where: { email: payload.email } })
    .then((user) => {
      if (user) {
        return done(null, user);
      }
      return done(null, false);

    }).catch((err) => {
      if (err) {
        return done(err, false);
      }

    });
}));

export default passport;

import * as express from 'express';
import { getCache } from './cache/Cache';
import { loginUser, registerUser } from './controller/AuthController';
import {
  deleteAccount, getAllUsers,
  getUserById, updateProfile } from './controller/UserController';
import passport from 'passport';
import {
  checkEmailAndPassword, checkIfBusExists, checkIfEmailExists, validateAddress,
  validateBusCredentials,
  validateLogin, validateRegistration} from './controller/validationController';
import { getAllBuses, getBusById, registerBus } from './controller/BusController';
import {
  bookUser, cancelPayment, checkIfThereIsSpace, checkIfUserBookedAlready,
  getSuccess, proceedToPayment} from './controller/Transactions';
import { sendInvoice } from './controller/EmailController';

const router = express.Router();

// AUTH ROUTES

/*
Stage 1 - validate core details,
Stage 2 - validate address fields,
Stage 3 - check if email exists
Stage 4  - if stage 1 ,2 & 3 ===success =>encrypt password and register user
 */
router.post('/register', validateRegistration, validateAddress, checkIfEmailExists, registerUser);
/*
Stage 1 - validate core details,
Stage 2 - check Email and Password,
Stage 3  - if stage 1 ,2  ===success =>generate token that lasts for one hour
 */
router.post('/login', validateLogin, checkEmailAndPassword, loginUser);

// USER ROUTES
/*
Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, retrieve data from cache if available
Stage 3 - if stage 2 = failure,retrieve data from db and store in cache
 */

router.get('/users', passport.authenticate('jwt', { session: false }), getCache, getAllUsers);
/*
Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, retrieve data from cache if available
Stage 3 - if stage 2 = failure,retrieve data from db and store in cache
 */
router.get('/users/:id', passport.authenticate('jwt', { session: false }), getCache, getUserById);
/*
Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, update users profile
*/
router.put('/users/:id', passport.authenticate('jwt', { session: false }), updateProfile);
/*
Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, delete users profile
*/
router.delete('/users/:id', passport.authenticate('jwt', { session: false }), deleteAccount);
/*
Stage 1 - Authenticate Users token
Stage 2 - Validate Bus input credentials
Stage 3  -Check if a similar bus exists
Stage 4 - if stage 3 =false, register that bus
 */
router.post('/bus', passport.authenticate('jwt', { session: false }),
            validateBusCredentials, checkIfBusExists, registerBus);
 /*
 Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, retrieve data from cache if available
Stage 3 - if stage 2 = failure,retrieve data from db and store in cache
  */
router.get('/buses', passport.authenticate('jwt', { session: false }), getCache, getAllBuses);
 /*
 Stage 1 - Authenticate the users token
Stage 2 - if stage 1= success, retrieve data from cache if available
Stage 3 - if stage 2 = failure,retrieve data from db and store in cache
  */
router.get('/bus/:id', passport.authenticate('jwt', { session: false }), getCache, getBusById);
/*
 Stage 1 - Authenticate the users token
 Stage 2- Check if user booked another bus
 Stage 3-  If stage 2 = no ,check if there is an empty seat on the bus
 Stage 4- if stage 3= true, Proceed to  checkout

 */
router.post('/pay/:uid/:bid', passport.authenticate('jwt', { session: false }),
            checkIfUserBookedAlready, checkIfThereIsSpace, proceedToPayment);
/*
Stage 1 - retrieve query data from url use it to process payement
Stage 2 - Grab transaction details and store them in DB
Stage 3 - Find user where email in transaction equals email in userdb and update records

*/
router.get('/success/:uid/:bid', getSuccess, bookUser, sendInvoice);

/*
Sage 1- Redirect users when they cancel Transaction
 */
router.get('/cancel/:uid/:bid', cancelPayment);

export default router;

import redis from 'redis';
import { NextFunction, Request, Response } from 'express';

const redisport: string = process.env.PORT_REDIS!;

const opts: redis.ClientOpts = {
  port: parseInt(redisport, 10),

};
const redisClient = redis.createClient(opts);

export const setCache = async (key: any, value: any) => {
  redisClient.set(key, JSON.stringify(value), 'EX', 5);

};

export const getCache = async (req: Request, res: Response, next: NextFunction) => {
  const key = req.route.path;
  redisClient.get(key, (err, reply) => {
    if (err) {
      res.status(400).send(err);
    }
    if (reply !== null) {
      return res.status(200).send(JSON.parse(reply));
    }
    next();
  });
};

import { PrismaClient } from '@prisma/client';
import { NextFunction, Request, Response } from 'express';
import bcrypt = require ('bcrypt');
import jsonwebtoken from 'jsonwebtoken';

const prisma = new PrismaClient();

export const registerUser = async (req: Request, res: Response) => {
  const password = req.body.password;
  await bcrypt.genSalt(10, (error, salt) => {
    if (error) throw error;
    // tslint:disable-next-line: no-floating-promises
    bcrypt.hash(password, salt, (err, encrypted) => {
      if (err) throw err;
      prisma.user.create({
        data: {
          username: req.body.username,
          password:`${encrypted}`,
          email: req.body.email,
          address:{
            create: {
              city:req.body.city,
              county:req.body.county,
            },
          },
        },
      })
          .then((register) => {

            return res.status(201).json(register);

          })
              .catch((err) => {
                return res.status(500).json(err);
              });
    });

  });

};

export const loginUser = async (req: Request, res: Response) => {
  await prisma.user.findOne({ where: { email: req.body.email } })
    .then((found) => {
      const payload = {
        email: found?.email,
        role: found?.role,
        username:found?.username,

      };
      const expires: number = 3600;

      jsonwebtoken.sign(
        payload, process.env.SECRET_OR_KEY!,
        { expiresIn: expires }, (err, encoded) => {
          if (encoded) {
            res.status(200).json({
              token:`${encoded}`,
            });
          }
          if (err) {
            return res.status(400).json({ message: 'Client Side Error' });
          }
        

        },

      );

    })
    .catch((err) => {
      return res.status(500).json({ message: err });
    });
};

import { NextFunction, Request, Response } from 'express';
import { PrismaClient } from '@prisma/client';
import { setCache } from '../cache/Cache';

const prisma = new PrismaClient();

export const getAllUsers = async (req: Request, res: Response) => {
  await prisma.user.findMany({
    include: {
      address: true,
    },
  })
  .then(async(json) => {
    await setCache(req.route.path, json);
    return res.status(200).json(json);
  })

    .catch((err) => {
      return res.status(500).json({ message: err });
    });

};

export const getUserById = async (req: Request, res: Response) => {
  await prisma.user.findOne({
    where: {
      id:parseInt(req.params.id, 10),
    },
    include: {
      address: {
        select: {
          id:true,
          county: true,
          city:true,

        },
      },

    },
  })
  .then(async(data) => {
    await setCache(req.route.path, data);
    return res.status(200).json(data);
  })

    .catch((err) => {
      return res.status(500).json({ message: err });
    });

};

export const updateProfile = async (req: Request, res: Response) => {
  const userid: number = parseInt(req.params.id, 10);
  await prisma.address.update({
    where: {
      userId:userid,
    },
    data: {

      county:req.body.county,
      city:req.body.city,

    },

  })

  .then((data) => {
    return res.status(200).json({ message:'User Data updated successfully' });
  })

    .catch((err) => {
      return res.status(500).json({ message: err });
    });
};

export const deleteAccount = async (req: Request, res: Response) => {
  const userid: number = parseInt(req.params.id, 10);
  await prisma.user.delete({
    where: {
      id:userid,
    },
  })
  .then((data) => {
    return res.status(200).json({ message:'Account Deleted successfully' });
  })

    .catch((err) => {

      return res.status(500).json({ message: err });
    });

};

import { validate } from 'class-validator';
import { NextFunction, Request, Response } from 'express';
import { Address } from '../validations/Address';
import { Register } from '../validations/Register';
import { PrismaClient } from '@prisma/client';
import { Login } from '../validations/Login';
import bcrypt = require ('bcrypt');
import { Bus } from '../validations/Bus';

const prisma = new PrismaClient();

export const validateRegistration = async (req: Request, res: Response, next: NextFunction) => {
  const register = new Register();
  register.username = req.body.username;
  register.password = req.body.password;
  register.email = req.body.email;
  register.confirmpassword = req.body.confirmpassword;

  const errors = await validate(register, { skipMissingProperties: true });
  if (errors.length > 0) {
    let errorText = Array();
    for (const errorItem of errors) {
      errorText = errorText.concat(errorItem.constraints);
    }
    return res.status(400).json(errorText);
  }
  next();

};
export const validateAddress = async (req: Request, res: Response, next: NextFunction) => {
  const address = new Address();
  address.city = req.body.city;
  address.county = req.body.county;

  const errors = await validate(address, { skipMissingProperties: true });
  if (errors.length > 0) {
    let errorText = Array();
    for (const errorItem of errors) {
      errorText = errorText.concat(errorItem.constraints);
    }
    return res.status(400).json(errorText);
  }
  next();

};

export const checkIfEmailExists = async (req: Request, res: Response, next: NextFunction) => {
  await prisma.user.findOne({ where:{ email:req.body.email } })
  .then((exists) => {
    if (exists) {
      return res.status(400).json({ email:'Email Already exists' });
    }
    if (!exists) {
      next();
    }

  }).catch((err) => {
    return res.status(500).json({ message: err });
  });

};

export const validateLogin = async (req: Request, res: Response, next: NextFunction) => {
  const login = new Login();
  login.email = req.body.email;
  login.password = req.body.password;

  const errors = await validate(login, { skipMissingProperties: true });
  if (errors.length > 0) {
    let errorText = Array();
    for (const errorItem of errors) {
      errorText = errorText.concat(errorItem.constraints);
    }
    return res.status(400).json(errorText);
  }
  next();

};

export const checkEmailAndPassword = async (req: Request, res: Response, next: NextFunction) => {
  await prisma.user.findOne({ where: { email: req.body.email } })
     .then((found) => {
       if (!found) {
         return res.status(404).send({ email: 'Email does not exist' });
       }

        // tslint:disable-next-line: no-floating-promises
       bcrypt.compare(req.body.password, found.password, (err , same) => {
         if (!same) {
           return res.status(403).send({ password: 'Passwords Do not Match' });

         }
         next();

       });
     })
  .catch((err) => {
    // tslint:disable-next-line:no-console
    console.log(err);
  });
};
export const validateBusCredentials = async (req: Request, res: Response, next: NextFunction) => {
  const bus = new Bus();
  bus.plates = req.body.plates;
  bus.routes = req.body.routes;
  bus.departureTime = req.body.departureTime;
  bus.arrivalTime = req.body.arrivalTime;
  bus.price = req.body.price;

  const errors = await validate(bus, { skipMissingProperties: true });
  if (errors.length > 0) {
    let errorText = Array();
    for (const errorItem of errors) {
      errorText = errorText.concat(errorItem.constraints);
    }
    return res.status(400).json(errorText);
  }
  next();

};

export const checkIfBusExists = async (req: Request, res: Response, next: NextFunction) => {
  await prisma.bus.findOne({
    where: {
      plates:req.body.plates,
    },
  })
    .then((result) => {
      if (result) {
        return res.status(403).json({ bus: 'Bus Already exists' });
      }
      next();

    }).catch((err) => {
      return res.status(500).json({ bus: err });
    });

};

import { IsDefined } from 'class-validator';

export class Address {
  @IsDefined({ message:  'County is Not defined' })
  county!: string;

  @IsDefined({ message:  'City is Not defined' })
  city!: string;

}

import { ArrayMaxSize, ArrayMinSize, IsArray, IsDefined, IsEmail, Length, ValidateNested } from 'class-validator';
import { Match } from '../custom/match.decorator';
import { Address } from './Address';

export class Register {

  @IsDefined({ message:  'Username is Not defined' })
  @Length(5, 20, { message:  'Username Should contain 5-20 characters ' })
  username!: string;

  @IsDefined({ message:  'Email is Not defined' })
  @IsEmail({}, { message:  'Incorrect Email Address' })
   email!: string;

  @IsDefined({ message:  'Password is Not defined' })
  @Length(6, 12, { message:  'Password should contain 6-12 characters' })
  password!: string;

  @Match('password', { message:  'Passwords do not match' })
  confirmpassword!: string;

}

import {
  registerDecorator, ValidationArguments, ValidationOptions,
  ValidatorConstraint, ValidatorConstraintInterface,
} from 'class-validator';

// tslint:disable-next-line:function-name
export function Match(property: string, validationOptions?: ValidationOptions) {
  return (object: any, propertyName: string) => {
    registerDecorator({
      propertyName,
      target: object.constructor,
      options: validationOptions,
      constraints: [property],
      validator: MatchConstraint,
    });
  };
}

@ValidatorConstraint({ name: 'Match' })
export class MatchConstraint implements ValidatorConstraintInterface {

  validate(value: any, args: ValidationArguments) {
    const [relatedPropertyName] = args.constraints;
    const relatedValue = (args.object as any)[relatedPropertyName];
    return value === relatedValue;
  }

}

import { IsEmail, IsDefined } from 'class-validator';
export class Login {

  @IsDefined({ message: 'Email is not defined' })
  @IsEmail({}, { message: 'Incorrect Email' })
   email!: string;

  @IsDefined({ message: 'Password is not defined' })
  password!: string;

